pub enum AttributeAccessPermissions {
    Readable,
    Writeable,
    ReadableAndWriteable,
}

pub enum AdStructure<'a> {
    Flags(Flags),
    ServiceUuids16(ServiceUuids),
    ServiceUuids32(ServiceUuids),
    ServiceUuids128(ServiceUuids),
    ServiceData16 {
        uuid: u16,
        data: &'a [u8],
    },
    CompleteLocalName(&'a str),
    ShortenedLocalName(&'a str),
    ManufacturerSpecificData {
        company_identifier: CompanyId,
        payload: &'a [u8],
    },
    Unknown {
        ty: u8,
        data: &'a [u8],
    },
}

pub enum Pdu<'a> {
    ConnectableUndirected {
        advertiser_addr: DeviceAddress,
        advertising_data: BytesOr,
    },
    ConnectableDirected {
        advertiser_addr: DeviceAddress,
        initiator_addr: DeviceAddress,
    },
    NonconnectableUndirected {
        advertiser_addr: DeviceAddress,
        advertising_data: BytesOr,
    },
    ScannableUndirected {
        advertiser_addr: DeviceAddress,
        advertising_data: BytesOr,
    },
    ScanRequest {
        scanner_addr: DeviceAddress,
        advertiser_addr: DeviceAddress,
    },
    ScanResponse {
        advertiser_addr: DeviceAddress,
        scan_data: BytesOr,
    },
    ConnectRequest {
        initiator_addr: DeviceAddress,
        advertiser_addr: DeviceAddress,
        lldata: ConnectRequestData,
    },
}

pub enum Pdu<'a, L> {
    DataCont {
        message: L,
    },
    DataStart {
        message: L,
    },
    Control {
        data: BytesOr,
    },
}

pub enum ControlPdu<'a> {
    ConnectionUpdateReq(&'a ConnectionUpdateData),
    ChannelMapReq(&'a ChannelMapReq),
    TerminateInd {
        error_code: Hex<u8>,
    },
    UnknownRsp {
        unknown_type: ControlOpcode,
    },
    FeatureReq {
        features_master: FeatureSet,
    },
    FeatureRsp {
        features_used: FeatureSet,
    },
    VersionInd {
        vers_nr: VersionNumber,
        comp_id: CompanyId,
        sub_vers_nr: Hex<u16>,
    },
    ConnectionParamReq(ConnectionParamRequest),
    ConnectionParamRsp(ConnectionParamRequest),
    Unknown {
        opcode: ControlOpcode,
        ctr_data: &'a [u8],
    },
}

pub enum RadioCmd {
    Off,
    ListenAdvertising {
        channel: AdvertisingChannel,
    },
    ListenData {
        channel: DataChannel,
        access_address: u32,
        crc_init: u32,
        timeout: bool,
    },
}
